<?php	if(has_post_thumbnail()){		$size = array(450,450);		$cover = phase_featured_image_url($size);	}else{		$cover = phase_first_image();	}	if($cover){		list($width,$height) = getimagesize($cover);		if($height > $width){			$orientation = 'vertical';		}else{			$orientation = 'horizontal';		}	}else{		$orientation='vertical text';	}	echo '<div class="entry '.$orientation.'"><a href="'.get_the_permalink().'"><div class="cover" style="background-image:url('.$cover.')"></div><div class="content"><small>'.get_the_time('M jS, Y').' &#183; by '.get_the_author().'</small><h2>'.get_the_title().'</h2><p>'.get_the_excerpt().'...</p></div></a></div>';?>